<script>
  import SongsPartVue from './SongsPart.vue'
export default {
  components:{
    SongsPartVue
  },
  data(){
    return{
      yyt:'Music Tube',
      fullHeight:document.documentElement.clientHeight
    }
  },
  watch:{
    fullHeight(val){//监控浏览器的高度变化
       if(!this.timer){
        this.fullHeight = val
        this.timer = true
        let that =  this
        setTimeout(function(){
            that.timer = false
        },400)
       }
    }
  },
  mounted(){
    this.get_height()
  },
  methods:{
    //动态获取浏览器高度
    get_height(){
        const that =  this
        window.onresize = () =>{
            return(()=>{
                window.fullHeight = document.documentElement.clientHeight
                that.fullHeight = window.fullHeight
            })
        }
    }
  }
}
</script>

